import React, {Component} from 'react';
import './App.css';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm'
import Rank from './components/Rank/Rank'
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import SignIn from './components/SignIn/SignIn'


const app = new Clarifai.App({
 apiKey: '7f8f59cc519d4fd6a47bd45939be5e2c'
});

const particlesOptions={
                particles: {
                  number:{
                      value:100,
                      density:{
                        enable:true,
                        value_area:800
                      }
                  },
                  line_linked: {
                      shadow: {
                        enable: true,
                        color: "#3CA9D1",
                        blur: 5
                      }

                  // interactivity:{
                  //     onhover:{
                  //       enable:true
                  //   },
                  // move:{
                  //   enable:false
                  // }
                  // }
                  // }
                }
              }
            }

class App extends Component {
  constructor(){
    super();
    this.state={
      input:'',
      imageUrl:'',
      box:{},
      route:'signin'
    }
  }

  calculateFaceLocation=(data)=>{
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box; //只要response中的那一组数
    const image =document.getElementById('inputImage');
    const width = Number(image.width);
    const height = Number(image.height);
    return {                                      //return an object
      leftCol:clarifaiFace.left_col*width,
      rightCol:width-clarifaiFace.right_col*width,
      topRow:clarifaiFace.top_row*height,
      bottomRow:height-clarifaiFace.bottom_row*height
    }
  }

  displayFacebox = (box_obj)=>{
    this.setState({box:box_obj});
  }

  onInputChange = (event)=>{
    this.setState({input:event.target.value});
  }

  onButtonSubmit = ()=>{
    this.setState({imageUrl:this.state.input})

    app.models
    .predict(
      Clarifai.FACE_DETECT_MODEL, 
      this.state.input)   //这里不能是this.state.imageUrl
    .then(response => this.displayFacebox(this.calculateFaceLocation(response)))
      // console.log(response.outputs[0].data.regions[0].region_info.bounding_box);
    .catch(err=>console.log(err))
      // there was an error
    }

    onRouteChange=(route)=>{
      this.setState({route:route})
    }


  render(){
    return ( 
      <div className="App">
        <Particles className='particles' params={particlesOptions}/> 
        <Navigation onRouteChange={this.onRouteChange}/>
        { this.state.route==='signin'
          ? <SignIn onRouteChange={this.onRouteChange}/>
          : <div>
              <Logo />
              <Rank/>
              <ImageLinkForm 
                onInputChange={this.onInputChange} 
                onButtonSubmit={this.onButtonSubmit}
              />
              <FaceRecognition box={this.state.box} imageUrl={this.state.imageUrl}/>
            </div>
        }
      </div>
    );
  } 
}

export default App;
